```js
// 网页中引入 require.js 及 main.js
<script src="js/require.js" data-main="js/main"></script>

// main.js 入口文件/主模块
// 首先用 config() 指定各模块 路径和引用名
require.config({
  baseUrl: 'js/lib',
  paths: {
    jquery: 'jquery.min',
    underscore: 'underscore.min',
  },
})

// 执行基本操作
