    let basicNum = 0
    let add = function(x, y) {
        return x + y
    }
    return {
        add,
        basicNum :basicNum,
    }
});
// 定义一个依赖underscore.js的模块
define(['underscore'], function(_) {
  let classify = function(list){
    _.countBy(list,function(num){
      return num > 30 ? 'old' : 'young'
    })
  }
  return {
    classify,
  }
})

